package abhigaya;

public class Pattern1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int i, j, n=5;
     
     for( i=1;i<=n;++i)
     {
         for(j=1;j<=i;++j)
         {
        	 System.out.print("*");
         }
          System.out.println(" ");// println takes to the next line
     }
	}

}
