package abhigaya;

public class Pattern7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
            int i,j,n=5;
         	
         	for(i=1;i<=n;++i)
         	{
         	 	for(j=i;j>=1;--j)
         	 	{
         	 		 System.out.print(j);
         	 	}
         	 	System.out.println();
         	}
	

}
}
