package programs;

public class SwitchExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int number=20;
    switch(number) {
    case 10: System.out.println("Apple");//here ur using SOP
    break;
    case 20:System.out.println("Ball");
    break;
    case 30:System.out.println("Cat");
    break;
    default: System.out.println("Not in 10,20,30");
    }
    
	}

}
